package sample;

public class PrimitiveDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 // Boolean - true or false
        boolean ONclass= true;
        System.out.println("boolean: " + ONclass);

        // Byte - 8-bit signed integer (-128 to 127)
        byte age = 21;
        System.out.println("byte: " + age);

        // Short - 16-bit signed integer 
        short classcount= 40;
        System.out.println("short: " + classcount);

        // Int - 32-bit signed integer
        int i = -127;
        System.out.println("int: " + i);

        // Long - 64-bit signed integer
        long PHno= 9866710852L;
        System.out.println("long: " + PHno);

        // Float - 32-bit single-precision floating-point number
        float pi = 3.14159f;
        System.out.println("float: " + pi);

        // Double - 64-bit double-precision floating-point number
        double topSpeed = 9.81;
        System.out.println("double: "  + topSpeed);

        // Char - 16-bit Unicode character
        char initial = 'B';
        System.out.println("char: " + initial);
	}

}
