package field;

public class TypeCast {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        //Narrow 
		int i = 10;
		long l = (long)i;
		System.out.println(l);
		double d = (double)i;
		System.out.println(d);
		float f = (float)i;
		System.out.println(f);
	}

}
